import React from 'react';
export default class Registration extends React.Component {
  constructor(props) {
      super(props);
      this.state = {
        message: ''
      };
    }
    onCreateEmployee=()=>{
      let empInfo={
            Email:this.refs.Email.value,
            Password:this.refs.Password.value
          };
          fetch('https://localhost:44385/api/Registration/Login',{
      method: 'POST',
      headers:{'Content-type':'application/json'},
        body: JSON.stringify(empInfo)
    }).then(r=>r.json()).then(res=>{
      if(res){
        this.setState({message:'Login Successful'});
      }
      
    });
      }
    render(){
      return(
        <div>
          <h2>Please Enter Employee Details...</h2>
         
          <p>
            <label>Email : <input type="text" ref="Email"></input></label>
          </p>
         
          <p>
            <label>Password : <input type="text" ref="Password"></input></label>
          </p>
          <button onClick={this.onCreateEmployee}>Login</button>
                
                <p>{this.state.message}</p>
                </div>
      )
    }
  }
