import React from 'react';
import axios from 'axios';

export default class PersonList extends React.Component {
  state = {
    name: '',
    First_Name:'',
    Last_Name: '',
     DOB: '',
     Country: '',
     Nationality: '',
    Email: '',
     Alternate_Email: '',
     Phone_no: '',
     Password: ''
  }

  handleChange = event => {
    this.setState({ name: event.target.value });
    this.setState({          First_Name: event.target.value});
     this.setState({         Last_Name: event.targetvalue});
     this.setState({         DOB: event.target.value});
    this.setState({        Country: event.target.Country.value});
     this.setState({       Nationality: event.target.value});
     this.setState({       Email: event.target.value});
     this.setState({       Alternate_Email: event.target.value});
     this.setState({      Phone_no: event.target.value});
     this.setState({       Password: event.target.value});
  }

  handleSubmit = event => {
    event.preventDefault();

    const user = {
      name: this.state.name,
      First_Name: this.state.First_Name,
      Last_Name: this.state.Last_Name,
      DOB: this.state.DOB,
      Country: this.state.Country,
      Nationality: this.state.Nationality,
      Email: this.state.Email,
      Alternate_Email: this.state.Alternate_Email,
      Phone_no: this.state.Phone_no,
      Password: this.state.Password
    };

    axios.post(`https://jsonplaceholder.typicode.com/users`, { user })
      .then(res => {
        alert('successful');
        console.log(res);
        console.log(res.data);
      })
  }

  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <label>
            Person Name:
            <input type="text" name="name" onChange={this.handleChange} />
          </label>
          <label>First_Name
           <input type="text" name="First_Name" required  />
           </label>
           <label>Last_Name
           <input type="text" name="Last_Name" required />
           </label>
           <label>DOB
           <input type="text" name="DOB" required />
           </label>
           <label>First_Name</label>
           <input type="text" name="First_Name" required />
           <label>Last_Name</label>
           <input type="text" name="Last_Name" required />
           <label>DOB</label>
           <input type="text" name="DOB" required />
           <label>Country</label>
           <input type="text" name="Country" required />
           <label>Nationality</label>
           <input type="text" name="Nationality" required />
           <label>Email</label>
           <input type="text" name="Email" required />
           <label>Alternate_Email</label>
           <input type="text" name="Alternate_Email" required />
           <label>Phone_no</label>
           <input type="text" name="Phone_no" required />
           <label>Password</label>
           <input type="text" name="Password" required />
           <label>Country</label>
           <input type="text" name="Country" required />
           <label>Nationality</label>
           <input type="text" name="Nationality" required />
           <label>Email</label>
           <input type="text" name="Email" required />
           <label>Alternate_Email</label>
           <input type="text" name="Alternate_Email" required />
           <label>Phone_no</label>
           <input type="text" name="Phone_no" required />
           <label>Password</label>
           <input type="text" name="Password" required />
          <button type="submit">Add</button>
        </form>
      </div>
    )
  }
}
