import React, { Component } from 'react'
import {Table,Button} from 'react-bootstrap';
import {Link} from 'react-router-dom';
import {update} from './update';
export default class Customer extends Component {
    constructor(props){
        super(props);
        this.state={deps:[],id:'',update:false
    }
    }
   
    //  componentDidMount(){
    //      this.refreshList();
    //  }
  
    refreshList(key){
       fetch('https://localhost:44385/api/Read/Get1?id='+key)
       .then(response=>response.json())
       .then(data=>{
        this.setState({deps:data});
       }
        );
       
       
    }
    render() {
        const{deps,First_Name,Last_Name,DOB,Country,Nationality,Email,Alternate_Email}=this.state;
        let updateClose =() => this.setState({upadteShow:false});

        return (
            <div>
                 <h3>Customer Details</h3>
                <center>
                <img src="https://cdn.thinglink.me/api/image/792008968430944257/1024/10/scaletowidth" className="r16" alt=""/>
               <br /><br /> <label>Enter your Customer _ID:</label>&nbsp;
                <input type="text" onChange={(event)=>this.refreshList(event.target.value)}/><br /><br />
                               </center>
                <center>
                 <div className="mt-5 d-flex justify-content-left">
                <Table className="mt-4" striped bordered hover size="sm">
                    <thead className="rand">
                        
                        <th>First_Name</th>
                        <th>Last_Name</th>
                        <th>DOB</th>
                        <th>Country</th>
                        <th>Nationality</th>
                        <th>Email</th>
                        <th>Alternate_Email</th>
                      <th>option</th>
                        
                    
                    </thead>
                    <tbody>
                    {deps.map(dep=>
                        <tr key={dep.CID}>
                        <td>{dep.First_Name}</td>
                        <td>{dep.Last_Name}</td>
                        <td>{dep.DOB}</td>
                        <td>{dep.Country}</td>
                        <td>{dep.Nationality}</td>
                        <td>{dep.Email}</td>
                        <td>{dep.Alternate_Email}</td>
                        <td>
                        <Button
className="mr-2" variant="info"
onClick= {()=> this.setState({updateShow:true, CID:dep.CID, First_Name:dep.First_Name,Last_Name:dep.Last_Name})}
>
    Edit
</Button>
                        </td>
                        
                        </tr>
                    )}
                    </tbody>
                </Table>
            </div>
            </center>
            <br />
            <div className="footer">
    <p>@copyright@2016 Rwenzori Airline.All right reserve</p>
  </div>
            </div>
            
        )
    }
}
