import React, { Component } from 'react';
import {Table } from 'react-bootstrap';
export default class Schedule extends Component {
    constructor(props){
        super(props);
        this.state={deps:[]}
    }
    componentDidMount(){
        this.refreshList();
    }
    refreshList(){
        this.setState({
            deps:[{"Day":"Monday","Source":"Delhi","Destination":"Bangalotre",
           "Depature":"08:00 am" ,"Arrival":"07:00am","price":6000}]
        })
    }
    render() {
        const{deps}=this.state;
        return (
            <div className="mt-5 d-flex justify-content-left">
                
                <Table className="mt-4" striped bordered hover size="sm">
                    <thead>
                        <th>Day</th>
                        <th>Source</th>
                        <th>Destination</th>
                        <th>Depature</th>
                        <th>Arrival</th>
                        <th>price</th>
                    </thead>
                    <tbody>
                        {deps.map(dep=>
                        <tr key={dep.day}>
                        <td>{dep.Day}</td>
                        <td>{dep.Source}</td>
                        <td>{dep.Destination}</td>
                        <td>{dep.Depature}</td>
                        <td>{dep.Arrival}</td>
                        <td>{dep.price}</td>
                        </tr>
                            )}
                    </tbody>
                </Table>
            </div>
        )
    }
}
