import {React,Component} from 'react';
import './Registration.css';
import {Link} from 'react-router-dom';
export default class Registration extends Component {
  constructor(props) {
      super(props);
      this.state = {
        message: '',
        

      };
    }
    
    
        
        
    
    
    onCreateEmployee=()=>{
      
      let empInfo={
            
           CID:this.refs.CID.value,
           Source:this.refs.Source.value,
           Destination:this.refs.Destination.value,
           Depature:this.refs.Depature.value,
           Return_date:this.refs.Return_date.value,
            
            
          
      
          };
          
          
          fetch('https://localhost:44385/api/Read/Create',{
      method: 'POST',
      headers:{'Content-type':'application/json'},
        body: JSON.stringify(empInfo)
    }).then(r=>r.json()).then(res=>{
      if(res){
        this.setState({message:'New User is Created Successfully'});
        
      //  this.props.history.push("/login");
      }
    });
      }
    
    render(){
      return(
        <div className="r4">
          <p>{this.state.message}</p>
          
          
          <center>
          <h2>Air Membership Registration</h2>
          
          </center>
          <form>          
            <p>
            <label>CID:<br /> <input type="text"  ref="CID" required></input></label>
            &emsp;
          
            <label>Source:<br /> <input type="text" ref="Source" required></input></label>
            &emsp;
            
            <label>Destination : <br /><input type="text" ref="Destination" required placeholder="dd-mm-yyyy"></input></label>
          </p>
          <p>
          &emsp;
            <label>Depature :<br /> <input type="text" ref="Depature" required></input></label>
            
           
          </p>
          <br />
          <p>
          <center>
          <button  onClick={this.onCreateEmployee} type="button" class="btn btn-primary">Submit</button>
          &emsp;
          <input class="btn btn-primary" type="reset" value="Reset"></input><br />
          
     
          <p>Registered Member <br />Click <Link to="/Login">Here</Link> to Login</p>
                </center>
                </p>
                </form>
                
                <div className="footer">
    <p>@copyright@2016 Rwenzori Airline.All right reserve</p>
  </div>
                </div>
                
                
      )
    }
  }
