import {React,Component} from 'react';
import './Registration.css';
import axios from 'axios';
import {Table} from 'react-bootstrap';
import {Link} from 'react-router-dom';
// import {Table} from 'react-bootstrap';
export default class Registration extends Component {
  constructor(props) {
      super(props);
      this.state = {
        message: '',
        deps:[],
        Status:'',
       errors:{
          First_Name:'',
          Last_Name:'',
          DOB:'',
          Country:'',
          Nationality:'',
          Email:'',
          Phone_no:'',
          Password:''
       },
        
        
        

      };
    }

   changeHandler=(event)=>{
     let name=event.target.name;
     let value=event.target.value;
     let errors=this.state.errors;

     switch (name) {
       case 'First_Name':
         errors.First_Name=value.length<5?"username must ne 5 character length":"";
         break;
         case 'Last_Name':
         errors.Last_Name=value.length<5?"username must ne 5 character length":"";
         break;
         case 'DOB':
          errors.DOB=value.length<5?"username must ne 5 character length":"";
          break;
          case 'Country':
            errors.Country=value.length<5?"username must ne 5 character length":"";
            break;
            case 'Nationality':
              errors.Nationality=value.length<5?"username must ne 5 character length":"";
              break;
              case 'Email':
                errors.Email=value.length<5?"username must ne 5 character length":"";
                break;
                case 'Alternate_Email':
                  errors.Alternate_Email=value.length<5?"username must ne 5 character length":"";
                  break;
                  case 'Phone_no':
                    errors.Phone_no=value.length<5?"username must ne 5 character length":"";
                    break;
                    case 'Password':
                      errors.Password=value.length<5?"username must ne 5 character length":"";
                      break;
       default:
         break;
     }
     console.log(this.state);
   };
    
   
    componentWillMount(){
      this.render5();
       
    }
    render5(){
    
        
    
       fetch('https://localhost:44385/api/Read/Get5?Email=roshan123@gmail.com')
       .then(response=>response.json())
       .then(data=>{
        this.setState({deps:data});
       }
        );
      }
   
 
  
        
    
    
    onCreateEmployee=()=>{
    
      const data={
        Email:this.refs.Email.value
      };
      axios.post('https://localhost:44385/api/Registration/Already',data)
      .then(res=>{
        console.log(res)
        if(res.data==="Unsuccessful"){
         alert("Email id Already exist in our Database");
         
        }
       
        else{
    



      let empInfo={
            
            First_Name:this.refs.First_Name.value,
            Last_Name:this.refs.Last_Name.value,
            DOB:this.refs.DOB.value,
            Country:this.refs.Country.value,
            Nationality:this.refs.Nationality.value,
            Email:this.refs.Email.value,
            Alternate_Email:this.refs.Alternate_Email.value,
            Phone_no:this.refs.Phone_no.value,
            Password:this.refs.Password.value,
           
          
      
          };
  
          
         
          fetch('https://localhost:44385/api/Registration/Create',{
      method: 'POST',
      headers:{'Content-type':'application/json'},
        body: JSON.stringify(empInfo)
    }).then(r=>r.json()).then(res=>{
      if(res){
        this.setState({message:'New User is Created Successfully'});
        
        //this.props.history.push("/login");
      }
    });
  
      }
    });
    }

    componentDidUpdate(){
      this.render5();
      
    }
    
    render(){
      const{deps}=this.state;
      const {errors}=this.state;
      return(
        <div className="r4">
          
          <p>{this.state.message}</p>
          
          
          <center>
          <h2>Air Membership Registration</h2>
          <Table className="mt-4" striped bordered hover size="sm">
                    <thead className="rand">
                        <th>CID</th>
                        
                    </thead>
                    <tbody>
                        {deps.map(dep=>
                        <tr key={dep.Email}>
                          
                        <td>{dep.CID}</td>
                        
                        </tr>
                            )}
                    </tbody>
                </Table>
          </center>
          <form>          
            <p>
            <label>First_Name :<br /> 
            <input type="text"  ref="First_Name" name="First_Name"  onChange={this.changeHandler} required></input></label>
            &emsp;
          <p></p>
            <label>Last_Name :<br /> <input type="text" ref="Last_Name" name="Last_Name" onChange={this.changeHandler} required></input></label>
            &emsp;
            
            <label>DOB : <br /><input type="text" ref="DOB" name="DOB"onChange={this.changeHandler} required placeholder="dd-mm-yyyy"></input></label>
          </p>
          <p>
          &emsp;
            <label>Country :<br /> <input type="text" name="Country" onChange={this.changeHandler} ref="Country" required></input></label>
            
            &emsp;
            <label>Nationality :<br /> <input type="text" name="Nationality"ref="Nationality" onChange={this.changeHandler}required></input></label>
            
            &emsp;
            <label>Email :<br /> <input type="email" ref="Email" name="Email"onChange={this.changeHandler} required></input></label>
          </p>
          <p>
          &emsp;
            <label>Alternate_Email : <br /><input type="text"onChange={this.changeHandler} name="Alternate_Email" ref="Alternate_Email" required></input></label>
            
            &emsp;
            <label>Phone_no :<br /> <input type="text" ref="Phone_no"onChange={this.changeHandler} name="Phone_no" required></input></label>
           
            &emsp;
            <label>Password :<br /> <input type="password" ref="Password"onChange={this.changeHandler} name="Password" required></input></label>
          </p>
          <br />
          <p>
          <center>
          <button  onClick={()=>{this.onCreateEmployee()}} type="button" class="btn btn-primary">Submit</button>
          &emsp;
          <input class="btn btn-primary" type="reset" value="Reset"></input><br />
          
     
          <p>Registered Member <br />Click <Link to="/Login">Here</Link> to Login</p>
                </center>
                </p>
               
                </form>
                
                <div className="footer">
    <p>@copyright@2016 Rwenzori Airline.All right reserve</p>
  </div>
                </div>
                
                
      )
    }
  }
