import React, { Component } from 'react'
import axios from 'axios';
export default class Search extends Component {

    constructor(props){
        super(props);
        this.state={
          Status:''
        }
      }
      handleSubmit=e=>{
        e.preventDefault();
    
        const data={
          Source:this.Source,
          Destinatiom:this.Destinatiom
        };
        axios.post('https://localhost:44385/api/Registration/Search',data)
        .then(res=>{
          console.log(res)
          if(res.data.Status==="Success"){
           alert("Matching Record Found ");
           this.props.history.push("/Ticket");
          }
          else{
            alert("Invalid User")
          }
          
        })
        .catch(err=>{
          console.log(err);
        })
      }
    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                 <h3>Search Flights</h3>
                <center>
               
                <img src="https://cdn.thinglink.me/api/image/792008968430944257/1024/10/scaletowidth" className="r16" alt=""/><br />
          <label>Source:</label><br /><input type ="text" onChange={e=>this.Source=e.target.value} /><br />
          <label>Destination</label><br/><input type="text" onChange={e=>this.Destinatiom=e.target.value}/><br /><br />
          <button className="btn btn-primary ">Search</button><br /><br />
           </center><br />
           </form>
           <div className="footer">
    <p>@copyright@2016 Rwenzori Airline.All right reserve</p>
  </div>
            </div>
        )
    }
}
