import React, { Component } from 'react'
import './Ticket.css';
import {Link} from 'react-router-dom';
export default class Ticket extends Component {
    constructor(props) {
        super(props);
        this.state = {
          message: ''
        };
      }
      onCreateEmployee=()=>{
        let empInfo={
              Day:this.refs.Day.value,
              Source:this.refs.Source.value,
              Destination:this.refs.Destination.value,
              Depature:this.refs.Depature.value,
              Arrival:this.refs.Arrival.value,
              price:this.refs.price.value
            };
            fetch('https://localhost:44385/api/Read/Create2',{
        method: 'POST',
        headers:{'Content-type':'application/json'},
          body: JSON.stringify(empInfo)
      }).then(r=>r.json()).then(res=>{
        if(res){
          alert("Added successful Successfully");
          this.props.history.push("/header");
          
        }
      });
        }
    render() {
        return (
            <div >
                
            <div className="ri">
            <p>{this.state.message}
        
            </p>
                <h2>Rwenzori Airlines</h2><br />
                <label>Day</label><br />
                <input type="text"ref="Day"/><br />
                <label>Source</label><br />
                <input type="text" ref="Source"/><br />
                 <label>Destination</label><br />
                <input type="text" ref="Destination"/><br />
                
                
                 <label>Depature Time:</label><br />
                 <input type="text" ref="Depature"/><br />
                 <label>Arrival Time</label><br />
                 <input type="text" ref="Arrival"/><br />
                 <label>price</label><br />
                 <input type="text" ref="price"/><br /><br />
                 <button onClick={this.onCreateEmployee} type="button">Submit</button>
            </div>
            <div className="ri">
                <div className="ros">
            <h2>control panel</h2><br />
            <ul>
            <li>
            <Link to ="/Customer">Home</Link><br/></li>
           <li> <Link to ="/update">Update profile Information</Link><br /></li>
           <li> <Link to ="/Schedule">View flight details</Link><br /></li>
           <li> <Link to ="/heeader">View purchase Tickets</Link><br /></li>
           <li> <Link to ="/Delete">Cancel</Link><br /></li>
           <li> <Link to ="/Ticket">Book flight Ticket</Link><br /></li>
           <li> <Link to ="/">Log out</Link><br />
            </li>
            </ul>
            </div>
            </div><br /><br />
            <div className="footer">
    <p>@copyright@2016 Rwenzori Airline.All right reserve</p>
  </div>
            </div>
        )
    }
}
