import React, { Component } from 'react'

export default class Registration extends Component {
    constructor(props){
        super(props);
        
        }
    handleSubmit(event){
        event.preventDefault();
        fetch('https://localhost:44385/api/Registration/Find',
        {
            method:'POST',
            headers:{
                'Accept':'application/json',
                'Content-Type':'application/json'
            },
            body:JSON.stringify({
                First_Name:event.target.First_Name.Value,
                Last_Name:event.target.Last_Name.Value,
                DOB:event.target.DOB.Value,
                Country:event.target.Country.Value,
                Nationality:event.target.Nationality.Value,
                Email:event.target.Email.Value,
                Alternate_Email:event.target.Alternate_Email.Value,
                Phone_no:event.target.Phone_no.Value,
                Password:event.target.Password.Value

            })
            
        })
        .then(res=>res.JSON())
        .then((result)=>{
           alert(result);
        },
        (error)=>{
            alert('Failed')
        }
        )
    }
    
    render() {
        return (
            <div>
                <h2>Login Page</h2>
                
                <form onSubmit={this.handleSubmit}>
        
        <p>First_Name:</p>
        <input
          type="text" value={this.state.First_Name}
        />
         <p>Last_Name:</p>
        <input
          type="text" value={this.state.Last_Name}
        />
         <p>DOB:</p>
        <input
          type="text" value={this.state.DOB}
        />
         <p>Country:</p>
        <input
          type="text" value={this.state.Country}
        />
         <p>Nationality:</p>
        <input
          type="text" value={this.state.Nationality}
        />
         <p>Email:</p>
        <input
          type="text" value={this.state.Email}
        />
         <p>Alternate_Email:</p>
        <input
          type="text" value={this.state.Alternate_Email}
        />
         <p>Phone_no:</p>
        <input
          type="text" value={this.state.Phone_no}
        />
         <p>Password:</p>
        <input type="text" value={this.state.Password}
        />
         <button type="Submit"  className="btn btn-success">Submit</button> 
      </form>
            </div>
        )
    }
}
