import React from 'react';
import axios from 'axios';
export default class App extends React.Component{
  constructor(){
    super()
    this.state={
      First_Name:"",
      Last_Name:"",
      DOB:"",
      Country:"",
      Nationality:"",
      Email:"",
      Alternate_Email:"",
      Phone_no:"",
      Password:"",
      loading:"",
      message:""
    }
  }
  dataChange(ev){
    this.setState({
      [ev.target.First_Name]:ev.target.value
    })
  }
  postData(ev){
    ev.preventDefault()
    const First_Name=this.state

    this.state({
      loading:true
    })
    const data={
      First_Name,
      Last_Name
    }
    axios.post('https://ptsv2.com/t/pvwla-1534789435/post',data)
    .then(response=>{
      console.log(response);
      this.setState({
      loading:false,
      message: response.data
    })
  })
    .catch(err=>{
      console.log(err);
      this.setState({
        loading:false
      })
    })
  }
  loadorShowing(){
    if(this.state.loading){
      return <p>Loading..</p>
    }
    else{
      return <p>{this.state.meeting}</p>
    }
  }
  render(){
    return(
      <div>
        <form>        
        <h1>Employee</h1>
        <input type="text" value="{this.state.First_Name}" name="First_Name" onChange={this.dataChange.bind(this)}/><br /> <br />
        <input type="text" value="{this" name="Last_Name" onChange={this.dataChange.bind(this)}/><br /> <br />
        <input type="text" value="" name="Last_Name" onChange={this.dataChange.bind(this)}/><br /> <br />
        <input type="text" value="" name="Last_Name" onChange={this.dataChange.bind(this)}/><br /> <br />
        <input type="text" value="" name="Last_Name" onChange={this.dataChange.bind(this)}/><br /> <br />
        <input type="text" value="" name="Last_Name" onChange={this.dataChange.bind(this)}/><br /> <br />
        <input type="text" value="" name="Last_Name" onChange={this.dataChange.bind(this)}/><br /> <br />
        <input type="text" value="" name="Last_Name" onChange={this.dataChange.bind(this)}/><br /> <br />
        <input type="submit"/>

        </form>
{this.loadorShowing()}
      </div>
    );
    }
  }
