import {React,Component} from 'react';
import './Registration.css';
import axios from 'axios';
import {Link} from 'react-router-dom';
import {Table} from 'react-bootstrap';
export default class Registration extends Component {
  constructor(props) {
      super(props);
      this.state = {
        message: '',
        deps:[],
        Status:''
        
        

      };
    }
    
roshan(){
  let data={
    Email:this.refs.First_Name.value
  }
  axios.post('https://localhost:44385/api/Registration/Already',data)
  .then(res=>{
    console.log(res)
    if(res.data.Status==="Success"){
     alert("Login successful");
     this.props.history.push("/Ticket");
    }
    else{

   
    
   
  // componentDidMount(){
  //   this.render5();
     
  // }
  // render5(){
  //    fetch('https://localhost:44385/api/Read/Get5?Email=rauniyar155.roshan@gmail.com')
  //    .then(response=>response.json())
  //    .then(data=>{
  //     this.setState({deps:data});
  //    }
  //     );
  //   }
        
        
    
    
    onCreateEmployee=()=>{
      
      let empInfo={
            
            First_Name:this.refs.First_Name.value,
            Last_Name:this.refs.Last_Name.value,
            DOB:this.refs.DOB.value,
            Country:this.refs.Country.value,
            Nationality:this.refs.Nationality.value,
            Email:this.refs.Email.value,
            Alternate_Email:this.refs.Alternate_Email.value,
            Phone_no:this.refs.Phone_no.value,
            Password:this.refs.Password.value
            
          
      
          };
  
          
         
          fetch('https://localhost:44385/api/Registration/Create',{
      method: 'POST',
      headers:{'Content-type':'application/json'},
        body: JSON.stringify(empInfo)
    }).then(r=>r.json()).then(res=>{
      if(res){
        this.setState({message:'New User is Created Successfully'});
        
       // this.props.history.push("/login");
      }
    });
  
      }
    }
  });
  }

      componentDidUpdate(){
        this.render5();
        
      }
    
    render(){
      const{deps}=this.state;
      return(
        <div className="r4">
          
          <p>{this.state.message}</p>
          
          
          <center>
          <h2>Air Membership Registration</h2>
          <Table className="mt-4" striped bordered hover size="sm">
                    <thead className="rand">
                        <th>CID</th>
                        
                    </thead>
                    <tbody>
                        {deps.map(dep=>
                        <tr key={dep.Email}>
                          
                        <td>{dep.CID}</td>
                        
                        </tr>
                            )}
                    </tbody>
                </Table>
          </center>
          <form>          
            <p>
            <label>First_Name :<br /> <input type="text"  ref="First_Name"  required></input></label>
            &emsp;
          
            <label>Last_Name :<br /> <input type="text" ref="Last_Name" required></input></label>
            &emsp;
            
            <label>DOB : <br /><input type="text" ref="DOB" required placeholder="dd-mm-yyyy"></input></label>
          </p>
          <p>
          &emsp;
            <label>Country :<br /> <input type="text" ref="Country" required></input></label>
            
            &emsp;
            <label>Nationality :<br /> <input type="text" ref="Nationality" required></input></label>
            
            &emsp;
            <label>Email :<br /> <input type="text" ref="Email" required></input></label>
          </p>
          <p>
          &emsp;
            <label>Alternate_Email : <br /><input type="text" ref="Alternate_Email" required></input></label>
            
            &emsp;
            <label>Phone_no :<br /> <input type="text" ref="Phone_no" required></input></label>
           
            &emsp;
            <label>Password :<br /> <input type="password" ref="Password" required></input></label>
          </p>
          <br />
          <p>
          <center>
          <button  onClick={()=>{this.roshan();this.onCreateEmployee();this.render5();}} type="button" class="btn btn-primary">Submit</button>
          &emsp;
          <input class="btn btn-primary" type="reset" value="Reset"></input><br />
          
     
          <p>Registered Member <br />Click <Link to="/Login">Here</Link> to Login</p>
                </center>
                </p>
               
                </form>
                
                <div className="footer">
    <p>@copyright@2016 Rwenzori Airline.All right reserve</p>
  </div>
                </div>
                
                
      )
    }
  }
