import React, { Component } from 'react'

export class Login extends Component {
  constructor(){
    super();
    this.state={
Email:'',
Password:''
    }
    this.Password=this.Password.bind(this);
    this.Email=this.Email.bind(this);
    this.login=this.login.bind(this);
  }
  Email(event){
    this.setState({Email:event.target.value})
  }
  Password(event){
    this.setState({Password:event.target.value})
  }
  login(event){
    debugger;
   fetch('https://localhost:44385/api/Registration/Login',{
      method:'POST',
      headers:{
        'Accept':'application/json',
        'Content-type':'application/json'},
      body:JSON.stringify({
     Email:this.state.Email,
     Password:this.state.Password
    })
  }).then((result)=>{
    console.log(result);
    if(result.Status==='Invalid')
    alert("invalid user");
    else
    this.props.history.push("/header");

  })
  }
  render() {
    return (
      <div className="roshan1">
        <center>
          <img src="" className="r15" alt=""/>
        <h2>Login Form</h2>
      <label>UserName</label><br />
        <input type="text" onChange={this.Email}/><br />
        <label>Password</label><br />
        <input type="text" onChange={this.Password}/><br />
        <button onClick={this.login} type="button">Submit</button>
        </center>
      </div>
    )
  }
}

export default Login
