import React, { Component } from 'react'

export default class update extends Component {
    constructor(props){
        super(props);
        this.state={deps:[],
            CID:''
    }
    }
   
   rand(){
    fetch('https://localhost:44385/api/Registration/Create',{
        method: 'Put',
        headers:{'Content-type':'application/json'},
          body: JSON.stringify(empInfo)
      }).then(r=>r.json()).then(res=>{
        if(res){
          this.setState({message:'New User is Created Successfully'});
          
          //this.props.history.push("/login");
        }
      });
   }   

  
    refreshList(key){
        
       fetch('https://localhost:44385/api/Read/Get1?id='+key)
       .then(response=>response.json())
       .then(data=>{
        this.setState({deps:data});
       }
        );
       
       
    }
    render() {
        const{deps}=this.state;
        

        return (
            <div>
                 <h3>Customer Details</h3>
                <center>
                <img src="https://cdn.thinglink.me/api/image/792008968430944257/1024/10/scaletowidth" className="r16" alt=""/>
               <br /><br /> <label>Enter your Customer _ID:</label>&nbsp;
                <input type="text" onChange={(event)=>this.refreshList(event.target.value)}/><br /><br />
                               </center>
            
    
                   
                            {deps.map(dep=>
                                <div>
                                    <center>                              
                                 <label>First_Name:</label><br />
                                <input type="text" value={dep.First_Name}/><br/>
                                <label>Last_Name</label><br/>
                                <input type="text" value={dep.Last_Name}/><br/>
                                <label>DOB</label><br/>
                                <input type="text" value={dep.DOB}/><br/>
                                <label>Country</label><br/>
                                <input type="text" value={dep.Country}/><br/>
                                <label>Nationality</label><br/>
                                <input type="text" value={dep.Nationality}/><br/>
                                <label>Alternate_Email</label><br/>
                                <input type="text" value={dep.Alternate_Email}/><br/>
                                <button onClick={()=>{this.rand()}}>Submit</button>
                                </center>
 
                                </div>
                                )}
                
                   
               
            
        
            
            <br />
            <div className="footer">
    <p>@copyright@2016 Rwenzori Airline.All right reserve</p>
  </div>
            </div>
            
        )
    }
}
