import React from 'react';
import axios from 'axios';
export default class App extends React.Component{
  constructor(){
    super()
    this.state={
      First_Name:"",
      Last_Name:"",
      DOB:"",
      Country:"",
      Nationality:"",
      Email:"",
      Alternate_Email:"",
      Phone_no:"",
      Password:"",
      loading:"",
      message:""
    }
  }
  dataChange(ev){
    this.setState({
      [ev.target.First_Name]:ev.target.value
    })
  }
  postData(ev){
    ev.preventDefault()
    const First_Name=this.state.First_Name
    const Last_Name=this.state.Last_Name
    const DOB=this.state.DOB
    const Country=this.state.Country
    const Nationality=this.state.

    this.state({
      loading:true
    })
    const data={
      First_Name,
      Last_Name,
      Country,
      Nationality,
      Email,
      Alternate_Email,
      Phone_no,
      Password
    }
    axios.post('https://ptsv2.com/t/pvwla-1534789435/post',data)
    .then(response=>{
      console.log(response);
      this.setState({
      loading:false,
      message: response.data
    })
  })
    .catch(err=>{
      console.log(err);
      this.setState({
        loading:false
      })
    })
  }
  loadorShowing(){
    if(this.state.loading){
      return <p>Loading..</p>
    }
    else{
      return <p>{this.state.meeting}</p>
    }
  }
  render(){
    return(
      <div>
        <form>        
        <h1>Employee</h1>
        <input type="text" value="{this.state.First_Name}" name="First_Name" onChange={this.dataChange.bind(this)}/><br /> <br />
        <input type="text" value="{this.state.Last_Name}" name="Last_Name" onChange={this.dataChange.bind(this)}/><br /> <br />
        <input type="text" value="{this.state.DOB}" name="DOB" onChange={this.dataChange.bind(this)}/><br /> <br />
        <input type="text" value="{this.state.Country}" name="Country" onChange={this.dataChange.bind(this)}/><br /> <br />
        <input type="text" value="{this.state.Nationality}" name="Nationality" onChange={this.dataChange.bind(this)}/><br /> <br />
        <input type="text" value="{this.state.Email}" name="Email" onChange={this.dataChange.bind(this)}/><br /> <br />
        <input type="text" value="{this.state.Alternate_Email}" name="Alternate_Email" onChange={this.dataChange.bind(this)}/><br /> <br />
        <input type="text" value="{this.state.Phone_no}" name="Phone_no" onChange={this.dataChange.bind(this)}/><br /> <br />
        <input type="submit"/>

        </form>
{this.loadorShowing()}
      </div>
    );
    }
  }
