import axios from 'axios';
import React, { Component } from 'react'
import {Link} from 'react-router-dom';
import './Login.css';

export default class Login extends Component {
  constructor(props){
    super(props);
    this.state={
      Status:'',
      msg:'',
      logstatus:'invalid'
    
    
    }
  }
  
  
  handleSubmit=e=>{
    e.preventDefault();

    const data={
      Email:this.Email,
      Password:this.Password
    };
    axios.post('https://localhost:44385/api/Registration/Login',data)
    .then(res=>{
      console.log(res)
      if(res.data.Status==="Success"){
       alert("Login successful");
       this.setState({logstatus:'valid'});
       this.props.history.push("/Ticket");
      }
      else{
        axios.post('https://localhost:44385/api/Registration/admin',data)
        .then(res=>{
          console.log(res)
          if(res.data.Status==="Success"){
            alert("Welcome Admin");
            this.props.history.push("/Journey");
           }
           else{
             alert ("invalid record");
           }
        })
      }
      
    })
    .catch(err=>{
      console.log(err);
    })
  }
  render() {
    return (
      <div className="r99">
       <div class="login">
  <h1>Login</h1>
    <form method="post">
      <input type="text" name="u" placeholder="Username" required="required" />
        <input type="password" name="p" placeholder="Password" required="required" />
        <button type="submit" class="btn btn-primary btn-block btn-large">Let me in.</button>
    </form>
</div>

         
        <div className="footer">
    <p>@copyright@2016 Roshan Airline.All right reserve</p>
  </div>
        
      
      
      </div>
    )
  }
}
