import './Schedule.css';
export default class update extends Component {
    constructor(props){
        super(props);
        this.state={deps:[]}
    }
    componentDidMount(){
        this.refreshList();
    }
    refreshList(){
       fetch('https://localhost:44385/api/Read/Get')
       .then(response=>response.json())
       .then(data=>{
        this.setState({deps:data});
       }
        );
       
       
    }
    render() {
        const{deps}=this.state;
