import axios from 'axios';
import React, { Component } from 'react'
import {Link} from 'react-router-dom';
import './Login.css';

export default class Login extends Component {
  constructor(props){
    super(props);
    this.state={
      Status:'',
      msg:'',
      logstatus:'invalid'
    
    
    }
  }
  
  
  handleSubmit=e=>{
    e.preventDefault();

    const data={
      Email:this.Email,
      Password:this.Password
    };
    axios.post('https://localhost:44385/api/Registration/Login',data)
    .then(res=>{
      console.log(res)
      if(res.data.Status==="Success"){
       alert("Login successful");
       this.setState({logstatus:'valid'});
       this.props.history.push("/Ticket");
      }
      else{
        axios.post('https://localhost:44385/api/Registration/admin',data)
        .then(res=>{
          console.log(res)
          if(res.data.Status==="Success"){
            alert("Welcome Admin");
            this.props.history.push("/Journey");
           }
           else{
             alert ("invalid record");
           }
        })
      }
      
    })
    .catch(err=>{
      console.log(err);
    })
  }
  render() {
    return (
      <div>
         <center> <h1> Student Login Form </h1> </center>   
    <form>  
        <div class="container">   
            <label>Username : </label>   
            <input type="text" placeholder="Enter Username" name="username" required />  
            <label>Password : </label>   
            <input type="password" placeholder="Enter Password" name="password" required />  
            <button type="submit">Login</button>   
            <input type="checkbox" checked="checked"> Remember me >0000000</input>  
            <button type="button" class="cancelbtn"> Cancel</button>   
             
        </div>   
    </form>     
        <div className="footer">
    <p>@copyright@2016 Roshan Airline.All right reserve</p>
  </div>
        
      
      </div>
    )
  }
}
