import React, { Component } from 'react'

export default class update extends Component {
    constructor(props){
        super(props);
        this.state={deps:[],
            CID:''
    }
    }
   
    handleChange = event => {
        this.setState({ CID: event.target.value });
      }
    //  componentDidMount(){
    //      this.refreshList();
    //  }
  
    refreshList(key){
        
       fetch('https://localhost:44385/api/Read/Get1?id='+1148)
       .then(response=>response.json())
       .then(data=>{
        this.setState({deps:data});
       }
        );
       
       
    }
    render() {
        const{deps}=this.state;
        

        return (
            <div>
                 <h3>Customer Details</h3>
                <center>
                <img src="https://cdn.thinglink.me/api/image/792008968430944257/1024/10/scaletowidth" className="r16" alt=""/>
               <br /><br /> <label>Enter your Customer _ID:</label>&nbsp;
                <input type="text" onChange={(event)=>this.refreshList(event.target.value)}/><br /><br />
                               </center>
            
                
                   
                            {deps.map(dep=>
                                <div>
                                <label>First_Name:</label>
                                <input type="text" value={dep.First_Name}/>
                                <label>Last_Name</label>
                                <input type="text" value={dep.Last_Name}/>
                                <label>DOB</label>
                                <input type="text" value={dep.DOB}/>
                                <label>Country</label>
                                <input type="text" value={dep.Country}/>
                                <label>Nationality</label>
                                <input type="text" value={dep.Nationality}/>
                                <label>Alternate_Email</label>
                                <input type="text" value={dep.Alternate_Email}/>
                                </div>
                                )}
                
                   
               
            
        
            
            <br />
            <div className="footer">
    <p>@copyright@2016 Rwenzori Airline.All right reserve</p>
  </div>
            </div>
            
        )
    }
}
