import React, { Component } from 'react'
import axios from 'axios';
import './search.css';
export default class Search extends Component {

    constructor(props){
        super(props);
        this.state={
          Status:''
        }
      }
      handleSubmit=e=>{
        e.preventDefault();
    
        const data={
          Source:this.Source,
          Destinatiom:this.Destinatiom
        };
        axios.post('https://localhost:44385/api/Registration/Search',data)
        .then(res=>{
          console.log(res)
          if(res.data.Status==="Success"){
           alert("Matching Flight Found ,start booking ticket");
          //  this.props.history.push("/Ticket");
          }
          else{
            alert("No matching Record Found")
          }
          
        })
        .catch(err=>{
          console.log(err);
        })
      }
    render() {
        return (
            <div className="madar1">
                <form onSubmit={this.handleSubmit}>
                 <h3>Search Flights</h3>
                <center>
               
                <img src="https://cdn.thinglink.me/api/image/792008968430944257/1024/10/scaletowidth" className="r16" alt=""/><br />
                <label>Flying From</label><br />
                <select onChange={this.divstatus} className="tty" >
              
                <option value="Bangalore">Bangalore</option>
                 <option value="Delhi">Delhi</option>
                 <option value="pune">pune</option>
                 <option value="Mumbai" >Mumbai</option>
                 <option value="patna" >Patna</option>
                 <option value="Mysore" >Mysore</option>
                 <option value="Hyderbad" >Hyderbad</option>
                </select>
              
                
          <button className="btn btn-primary ">Search</button><br /><br />
           </center><br />
           </form>
           <div className="footer">
    <p>@copyright@2016 Rwenzori Airline.All right reserve</p>
  </div>
            </div>
        )
    }
}
