import {React,Component} from 'react';
import './Registration.css';
import {Link} from 'react-router-dom';
export default class Journey extends Component {
  constructor(props) {
      super(props);
      this.state = {
        message: '',
       

      };
    }
    
    
        
        
    
    
    onCreateEmployee=()=>{
      let empInfo={
            
            Day:this.refs.Day.value,
            Source:this.refs.Source.value,
            Destinatiom:this.refs.Destinatiom.value,
            Depature:this.refs.Depature.value,
            Arrival:this.refs.Arrival.value,
            price:this.refs.price.value,
            
            
          
      
          };
          
          
        
          fetch('https://localhost:44385/api/Read/Create2',{
      method: 'POST',
      headers:{'Content-type':'application/json'},
        body: JSON.stringify(empInfo)
    }).then(r=>r.json()).then(res=>{
      if(res){
        this.setState({message:'New User is Created Successfully'});
        
       // this.props.history.push("/login");
      }
    });
      }
    render(){
      return(
        <div className="r4">
          <p>{this.state.message}</p>
          
          
          <center>
          <h2>Air Membership Registration</h2>
          
          </center>
          <form>          
            <p>
            <label>Day :<br /> <input type="text"  ref="Day" required></input></label>
            <br />
          
            <label>Source :<br /> <input type="text" ref="Source" required></input></label>
            <br />
            
            <label>Destination : <br /><input type="text" ref="Destinatiom" required placeholder="dd-mm-yyyy"></input></label>
          </p>
          <p>
          &emsp;
            <label>Depature :<br /> <input type="text" ref="Depature" required></input></label>
            
            &emsp;
            <label>Arrival :<br /> <input type="text" ref="Arrival" required></input></label>
            
            &emsp;
            <label>price :<br /> <input type="text" ref="price" required></input></label>
          </p>
          
          <br />
          <p>
          <center>
          <button  onClick={this.onCreateEmployee} type="button" class="btn btn-primary">Submit</button>
          &emsp;
          <input class="btn btn-primary" type="reset" value="Reset"></input><br />
          
     
          
                </center>
                </p>
                </form>
                
                <div className="footer">
    <p>@copyright@2016 Rwenzori Airline.All right reserve</p>
  </div>
                </div>
                
                
      )
    }
  }
