import React, {Component} from 'react';
import {Button,  Form} from 'react-bootstrap';



export  default class update extends Component{

    constructor(props){
        super(props);
        
        this.handleSubmit = this.handleSubmit.bind(this);
    }

   

      handleSubmit(event){
        event.preventDefault();
        fetch('http://localhost:49902/api/Registration/Update',{
          method:'PUT',
          headers:{
            'Accept':'application/json',
            'Content-Type':'application/json'
          },
          body:JSON.stringify({
            CID:event.target.CID.value,
            First_Name: event.target.First_Name.value,
            Last_Name:event.target.Last_Name.value,
            DOB:event.target.DOB.value,
            Country:event.target.Country.value,
            Nationality:event.target.Nationality.value,
            Email:event.target.Email.value,
            Alternate_Email:event.target.Alternate_Email.value,
            Phone_no:event.target.Phone_no.value,
            Password:event.target.Password.value
          })
        })
        .then(res=> res.json())
        .then((result)=>
        {
            alert(result);
            
        },
        (error)=>{
          alert('Failed')
          
        }
        )
    }


    render(){
        return(
          <div className="container">



        
         
        
              <Form onSubmit={this.handleSubmit}>


              <Form.Group controlId="CID">
              <Form.Label>CID</Form.Label>
              <Form.Control
                type="text"
                name="CID"
                required
                disabled
                defaultValue = {this.props.CID}
                placeholder="CID"
               />
              </Form.Group>

              <Form.Group controlId="First_Name">
              <Form.Label>First_Name</Form.Label>
              <Form.Control
                type="text"
                name="First_Name"
                required
                defaultValue = {this.props.First_Name}
                placeholder="First_Name"
               />
              </Form.Group>

              <Form.Group controlId="Last_Name">
              <Form.Label>Last_Name</Form.Label>
              <Form.Control
                type="text"
                name="Last_Name"
                required
                defaultValue = {this.props.Last_Name}
                placeholder="Last_Name"
               />
              </Form.Group>
              
              <Form.Group controlId="DOB">
              <Form.Label>DOB</Form.Label>
              <Form.Control
                type="text"
                name="DOB"
                required
                defaultValue = {this.props.DOB}
                placeholder="DOB"
               />
              </Form.Group>
               
              <Form.Group controlId="Country">
              <Form.Label>Country</Form.Label>
              <Form.Control
                type="text"
                name="Country"
                required
                defaultValue = {this.props.DOB}
                placeholder="DOB"
               />
              </Form.Group>







              <Form.Group>
                  <Button variant="primary" type="submit">
                  Update Department
                  </Button>
              </Form.Group>
              </Form>
    
          

         

        

      </div>
        );
    }




}
