import React, { Component } from 'react'
import './Ticket.css';
import {Link} from 'react-router-dom';
export default class Ticket extends Component {
    constructor(props) {
        super(props);
        this.state = {
          message: ''
        };
      }
      onCreateEmployee=()=>{
        let empInfo={
              
              First_Name:this.refs.So.value,
              Last_Name:this.refs.Last_Name.value,
              DOB:this.refs.DOB.value,
              Country:this.refs.Country.value,
            };
            fetch('https://localhost:44385/api/Registration/Create',{
        method: 'POST',
        headers:{'Content-type':'application/json'},
          body: JSON.stringify(empInfo)
      }).then(r=>r.json()).then(res=>{
        if(res){
          this.setState({message:'New User is Created Successfully'});
        }
      });
        }
    render() {
        return (
            <div >
                <p>{this.state.message}</p>
            <div className="ri">
                
                <h2>Rwenzori Airlines</h2><br />
                
                <label>Flying From</label><br />
                <input type="text" ref="Source"/>
                 <label>Moving to</label><br />
                <input type="text" ref="Destination"/>
                
                
                 <label>Depature Date:</label><br />
                 <input type="text" ref="Depature" placeholder="dd-mm-yyyy"/><br />
                 <label>Return Date:(Leave blank if one way trip)</label><br />
                 <input type="text" ref="Depature" placeholder="dd-mm-yyyy"/><br /><br />
                 <button onClick={this.onCreateEmployee} type="button">Submit</button>
            </div>
            <div className="ri">
                <div className="ros">
            <h2>control panel</h2><br />
            <ul>
            <li>
            <Link to ="/header">Home</Link><br/></li>
           <li> <Link to ="/header">Update profile Information</Link><br /></li>
           <li> <Link to ="/Schedule">View flight details</Link><br /></li>
           <li> <Link to ="/heeader">View purchase Tickets</Link><br /></li>
           <li> <Link to ="/header">Cancel</Link><br /></li>
           <li> <Link to ="/Ticket">Book flight Ticket</Link><br /></li>
           <li> <Link to ="/">Log out</Link><br />
            </li>
            </ul>
            </div>
            </div>
            </div>
        )
    }
}
