import React, { Component } from 'react'
import {Button} from 'react-bootstrap';
import axios from 'axios';
import './search.css';
export default class Search extends Component {

    constructor(props){
        super(props);
        this.state={
          Status:''
        }
      }
      handleSubmit=e=>{
        e.preventDefault();
    
        const data={
          Source:this.Source,
          Destinatiom:this.Destinatiom
        };
        axios.post('https://localhost:44385/api/Registration/Search',data)
        .then(res=>{
          console.log(res)
          if(res.data.Status==="Success"){
           alert("Matching Flight Found ,start booking ticket");
          //  this.props.history.push("/Ticket");
          }
          else{
            alert("No matching Record Found")
          }
          
        })
        .catch(err=>{
          console.log(err);
        })
      }
    render() {
        return (
            <div className="madar1">
              <fieldset>
                <form onSubmit={this.handleSubmit}>
                 <h3>Search Flights</h3>
                 <label>Flight From:</label>&emsp;<input type="text" placeholder="From city" className="r15"></input>&emsp;&emsp;&emsp;
                 <label>Flight To:</label>&emsp;<input type="text" placeholder="From to" className="" ></input>&emsp;&emsp;&emsp;
                 <Button>search</Button>
                 </form>
                 </fieldset>
           
           <div className="footer">
    <p>@copyright@2016 Rwenzori Airline.All right reserve</p>
  </div>
            </div>
        )
    }
}
